
// ;console.log( `user.js` )
const CA_user_o =
{
  img_o: null,
  collectionIdb_i: null,
  galleryIdb_i: null,
  dialogs_o:
  {
    format: `Le fichier sélectionné n'est pas au format JPEG`,
    size:   `La taille de l'image est trop grande: impossible de l'analyser`,
    reader: `Une erreur s'est produite en chargeant le fichier image: impossible de l'analyser`,
  },
}

const userImgGetInfo = () =>
{
  const info_o =
  {
    id_s:  document.getElementById( 'ca_form_input_is_user_id' ).value,
    tag_s: document.getElementById( 'ca_form_input_is_user_tag' ).value,
  }
  return info_o 
}

const userImgToDataUrl = ( files_o ) =>
{
  if ( !files_o.files.length ) return
  const badFileType = ( msg_s ) => document.getElementById( 'ca_form_img_label_is_user_file' ).innerHTML = msg_s || ''
  const afile_o = files_o.files[0]
  if ( !afile_o.type.match( 'image/jpeg' ) )
  {
    badFileType( CA_user_o.dialogs_o.format )
    document.getElementById( 'ca_form_img_thumb_is_user_file' ).src = ''
    return
  }

  const reader_i = new FileReader()
  reader_i.onerror = ( _read_e_ ) =>
  {
    reader_i.abort()
    return badFileType( CA_user_o.dialogs_o.reader )
  }

  reader_i.onload = ( read_e ) =>
  {
    const maxdim = DOM_getRootVar( '--MEDIA_USER_IMG_DIM_MAX' )
    const img_e = new Image()
    img_e.onload = ( load_e ) =>
    {
      const width  = load_e.target.naturalWidth
      const height = load_e.target.naturalHeight
      let src_s = ''
      let msg_s = afile_o.name
      if ( (width * height) > (maxdim * maxdim) ) msg_s = CA_user_o.dialogs_o.size
      else src_s = img_e.src
      badFileType( msg_s )
      if ( src_s === '' ) return
      document.getElementById( 'ca_form_img_thumb_is_user_file' ).src = src_s
      CA_user_o.img_o =
      {
        file_s: afile_o.name,
        id_s:   '',
        tag_s:  '',
        note_s: '',
        width:  width,
        height: height,
        src_s:  read_e.target.result,
      }
    }
    img_e.src = read_e.target.result
  }
  reader_i.readAsDataURL( afile_o )
}

const userImgFileToIDB = ( dialog_s ) =>
{
  if ( !dialog_s || !dialog_s.includes( 'dialog_option_0' ) ) return   //: 'ca_user_file_dialog_option_0' is OK button
  const info_o = userImgGetInfo()
  CA_user_o.img_o.id_s = info_o.id_s
  CA_user_o.img_o.tag_s = info_o.tag_s || 'collection'    //: we need a tag to be able to update
  const key_n = Date.now()    //: use Date.now as IDB key
  CA_user_o.collectionIdb_i.set( key_n, CA_user_o.img_o )
    .then( () => window.location.reload() )
}

const userImgInit = ( init_f ) =>
{
  let max = 0
  CA_user_o.galleryIdb_i.get( 'item' )
  .then( ( key_n ) =>
    {
      CA_user_o.collectionIdb_i.get( key_n )
        .then( item_o =>
        {
          if ( item_o )
          {
            const img_e = new Image( item_o.width, item_o.height )
            img_e.src = item_o.src_s
            const user_o =
            {
              file_s: item_o.file_s,
              width:  item_o.width,
              height: item_o.height,
            }
            img_e.onload = load_e => init_f( img_e, user_o )
          }
        } )
    } )
}

;( () =>
{
  if ( CA_user_o.collectionIdb_i === null ) CA_user_o.collectionIdb_i = new KVIdb( 'UserCollection', 'collection' )
  if ( CA_user_o.galleryIdb_i === null ) CA_user_o.galleryIdb_i = new KVIdb( 'UserGallery', 'gallery' )
} )()


